import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  //TODO: add typescript
  // TODO: set use state inner perens to structure of api
  // TODO: change name to credit card something or another
  const [isValid, setIsValid] = useState();
  const [creditCardNumber, setCreditCardNumber] = useState('');
  const handleSubmit = (event) => {
    event.preventDefault();
    const formattedCreditCardNumber = creditCardNumber.replace(/[ ,-]/g, '');

    console.log('submitted', formattedCreditCardNumber);

    fetch('/credit-card-validity')
      .then((response) => response.json())
      .then((data) => {
        setIsValid(data.isValid);
      });
  };

  // TODO: remove
  // useEffect(() => {
  //   fetch('/api')
  //     .then((response) => response.json())
  //     .then((data) => {
  //       setBackendData(data);
  //     });
  // }, []);

  return (
    <div className="App">
      {isValid ? <p>{JSON.stringify(isValid)}</p> : '...loading'}
      <form
        onSubmit={(event) => {
          handleSubmit(event);
        }}
      >
        {/* <form method="post" onSubmit={handleSubmit}> */}
        <label htmlFor="cc-number">Credit Card Number: </label>
        <input
          type="text"
          id="credit-card"
          onChange={(event) => {
            setCreditCardNumber(event.target.value);
          }}
          autoComplete="cc-number"
          inputMode="numeric"
          name="cc-number"
          //credit cards range from 12-19 characters. Allowing max length up to 25 to account for spacing and dashes users may enter
          maxLength="25"
          minLength="12"
          // TODO: figure out error with pattern in console.
          //regex to only allow digits with spaces and dashes
          // pattern="(\d[ -]*){12,25}"
          title="Credit card numbers must be between 12 and 19 digits long with no spaces or dashes."
          value={creditCardNumber}
        />
        <input type="submit" value="Submit" />
      </form>
      <p>
        Credit card number {creditCardNumber} is{' '}
        <strong>{isValid ? 'valid' : 'invalid'}</strong>.
      </p>
    </div>
  );
}

export default App;
