import React, { useEffect, useState } from 'react';

function App() {
  // TODO: set use state inner perens to structure of api
  // TODO: change name to credit card something or another
  const [backendData, setBackendData] = useState([{}]);

  useEffect(() => {
    fetch('/api')
      .then((response) => response.json())
      .then((data) => {
        setBackendData(data);
      });
  }, []);

  return (
    <div>
      {backendData ? <p>{JSON.stringify(backendData)}</p> : '...loading'}
      <form>
        <label for="cc-number">Credit Card Number: </label>
        <input
          type="text"
          id="credit-card"
          name="cc-number"
          // credit card numbers range from 12-19 characters
          pattern="\d{12,19}"
          placeholder="####-####-####-####"
          oninvalid="this.setCustomValidity('invalid.')"
        />
        <input type="submit" value="Submit" />
      </form>
    </div>
  );
}

export default App;
